INSERT INTO mytable(Id,Lien,Prénom,Catégorie,Sexe,AgeDate_de_naissance,Description,Badges1,Badges_des1,Badges2,Badges_des2,Badges3,Badges_des3,Adresse_refuge) VALUES (2,'https://pattesenrond.fr/charly-ne-le-19-04-2021/','Charly','Chats','Mâle','19/04/2021','J’ai été retrouvé à l''âge de 2 mois derrière un buisson, à côté d’un arrêt de bus, en bon état mais couvert de poux. Le refuge m’a accueilli et m''a permis de rejoindre une famille d’accueil avec d’autres chats. Finies les griffures et les morsures j’ai appris à devenir un chaton adorable.','Environnement sécurisé',NULL,'Contact avec d''autres',NULL,'Jouer',NULL,'164 Rue Rousseau Vaudran, 77190 Dammarie-les-Lys');
INSERT INTO mytable(Id,Lien,Prénom,Catégorie,Sexe,AgeDate_de_naissance,Description,Badges1,Badges_des1,Badges2,Badges_des2,Badges3,Badges_des3,Adresse_refuge) VALUES (3,'https://pattesenrond.fr/pitou-ne-le-01-04-2015/','Pitou','Chats','Mâle','01/04/2015','Je vivais sur un parking et j’étais nourri par les habitants de mon quartier. À mon arrivée au refuge j’étais très maigre et mon poil était sale. J’ai rejoint une famille d’accueil où je me suis montré sociable avec chats, chiens et enfants. Je rêve de trouver un foyer qui prendra soin de moi jusqu''à la fin de ma vie.','Environnement sécurisé',NULL,'Contact avec d''autres',NULL,'Caresses',NULL,'164 Rue Rousseau Vaudran, 77190 Dammarie-les-Lys');
INSERT INTO mytable(Id,Lien,Prénom,Catégorie,Sexe,AgeDate_de_naissance,Description,Badges1,Badges_des1,Badges2,Badges_des2,Badges3,Badges_des3,Adresse_refuge) VALUES (4,'https://pattesenrond.fr/popi-ne-le-1-01-2018/','Popi','Chats','Mâle','01/01/2018','J’ai vécu dans la rue, mon maître m’a sans doute abandonné ou perdu mais je suis en bonne santé. Actuellement en famille d’accueil,  très sociable, je recherche une famille d’adoption pour me couvrir d’amour.','Environnement sécurisé',NULL,'Contact avec d''autres',NULL,'Enfant(s)',NULL,'164 Rue Rousseau Vaudran, 77190 Dammarie-les-Lys');
INSERT INTO mytable(Id,Lien,Prénom,Catégorie,Sexe,AgeDate_de_naissance,Description,Badges1,Badges_des1,Badges2,Badges_des2,Badges3,Badges_des3,Adresse_refuge) VALUES (5,'https://pattesenrond.fr/dolly-nee-le-25-05-2012/','Dolly','Chats','Femelle','25/05/2012','Mes anciens maîtres ont déménagé et ils ne m’avaient pas habitué à ce nouvel environnement, je me suis donc perdue, j’étais sale et maigre quand l’association m’a trouvée. Durant 4 ans, j’ai vécu la vie d’une chatte errante car mes anciens maîtres ne voulaient pas me récupérer et refusaient de me céder à l’association. Je suis revenu devant les portes de l’association et ils m’ont pris sous leurs ailes. Je recherche une famille où je pourrais pleinement m’épanouir.','Environnement sécurisé',NULL,'Calme',NULL,'Jouer',NULL,'164 Rue Rousseau Vaudran, 77190 Dammarie-les-Lys');
INSERT INTO mytable(Id,Lien,Prénom,Catégorie,Sexe,AgeDate_de_naissance,Description,Badges1,Badges_des1,Badges2,Badges_des2,Badges3,Badges_des3,Adresse_refuge) VALUES (6,'https://pattesenrond.fr/rouky-ne-le-01-07-2018/','Rouky','Chats','Mâle','01/07/2018','Dans mon ancien foyer, un membre de la famille a déclenché une allergie. Je suis sociable et plein d’énergie.','Environnement sécurisé',NULL,'Extérieur',NULL,'Contact avec d''autres',NULL,'164 Rue Rousseau Vaudran, 77190 Dammarie-les-Lys');
INSERT INTO mytable(Id,Lien,Prénom,Catégorie,Sexe,AgeDate_de_naissance,Description,Badges1,Badges_des1,Badges2,Badges_des2,Badges3,Badges_des3,Adresse_refuge) VALUES (7,'https://pattesenrond.fr/guimauve-ne-le-1-08-2015/','Guimauve','Chats','Mâle','01/08/2015','Je n’ai pas eu une vie facile, j’ai été abandonné.  À mon arrivée au refuge, j’étais craintif, j’avais peur des mains et il m’arrivait de mordre. Mon corps était couvert de griffures et de morsures, j’ai été testé positif au FIV (sida du chat) et au calicivirus. Peu à peu, j’ai compris que j’aimais les caresses de ma famille d’accueil en qui j’ai confiance.','Environnement sécurisé',NULL,NULL,NULL,NULL,NULL,'164 Rue Rousseau Vaudran, 77190 Dammarie-les-Lys');
INSERT INTO mytable(Id,Lien,Prénom,Catégorie,Sexe,AgeDate_de_naissance,Description,Badges1,Badges_des1,Badges2,Badges_des2,Badges3,Badges_des3,Adresse_refuge) VALUES (8,'https://pattesenrond.fr/oreo-ne-le-10-07-2020/','Oréo','Chats','Mâle','10/07/2020','Je suis né dans la rue mais je suis un petit chaton adorable. Ma maman Rubis est aussi au refuge, elle a fait une portée de 6 chatons mais seulement 4 dont moi ont été retrouvés.','Environnement sécurisé',NULL,'Contact avec d''autres',NULL,'Caresses',NULL,'164 Rue Rousseau Vaudran, 77190 Dammarie-les-Lys');
